def get_json_response(url):
    response = requests.get(url)
    return response.json()

def print_formatted_countdown(millis_time):
    hours = millis_time // 3600000
    minutes = (millis_time % 3600000) // 60000
    seconds = (millis_time % 60000) // 1000
    print("The game will start in {} hours, {} minutes, {} seconds!".format(hours, minutes, seconds))

def clear_screen():
    os.system('cls' if os.name=='nt' else 'clear')

def capped_length_lines(full_line, line_length=40, delimiter=' '):
    my_list = []
    temp_str = ""
    str_list = full_line.split(delimiter)
    for word in str_list:
        if len(temp_str) + len(word) > line_length:
            my_list.append(temp_str)
            temp_str = ""
        temp_str += word + delimiter

    if temp_str:
        my_list.append(temp_str)

    return my_list